-- File: ~/.config/nvim/lua/plugins/lspconfig.lua

return {
	"neovim/nvim-lspconfig",
	dependencies = {
		"williamboman/mason.nvim",
		"williamboman/mason-lspconfig.nvim",
		{ "j-hui/fidget.nvim", opts = {} },
		{ "folke/neodev.nvim", opts = {} },
	},
	config = function()
		local on_attach = function(client, bufnr)
			vim.notify("LSP attached: " .. client.name .. " to buffer " .. bufnr, vim.log.levels.INFO)

			local map = function(keys, func, desc)
				vim.keymap.set("n", keys, func, { buffer = bufnr, desc = "LSP: " .. desc })
			end

			-- All your keymaps remain the same
			map("gd", require("telescope.builtin").lsp_definitions, "[G]oto [D]efinition")
			map("gr", require("telescope.builtin").lsp_references, "[G]oto [R]eferences")
			map("gI", require("telescope.builtin").lsp_implementations, "[G]oto [I]mplementation")
			map("D", require("telescope.builtin").lsp_type_definitions, "Type [D]efinition")
			map("gD", vim.lsp.buf.declaration, "[G]oto [D]eclaration")
			map("ds", require("telescope.builtin").lsp_document_symbols, "[D]ocument [S]ymbols")
			map("ws", require("telescope.builtin").lsp_dynamic_workspace_symbols, "[W]orkspace [S]ymbols")
			map("rn", vim.lsp.buf.rename, "[R]e[n]ame")
			map("ca", vim.lsp.buf.code_action, "[C]ode [A]ction")
			map("K", vim.lsp.buf.hover, "Hover Documentation")

			if client:supports_method("textDocument/documentHighlight") then
				vim.api.nvim_create_autocmd({ "CursorHold", "CursorHoldI" }, {
					buffer = bufnr,
					callback = vim.lsp.buf.document_highlight,
				})
				vim.api.nvim_create_autocmd({ "CursorMoved", "CursorMovedI" }, {
					buffer = bufnr,
					callback = vim.lsp.buf.clear_references,
				})
			end
		end

		vim.api.nvim_create_autocmd("LspAttach", {
			group = vim.api.nvim_create_augroup("UserLspConfig", {}),
			callback = function(args)
				local client = vim.lsp.get_client_by_id(args.data.client_id)
				local bufnr = args.buf

				-- You may want to disable document highlighting for large files
				if client and client.name ~= "tsserver" then
					on_attach(client, bufnr)
				end
			end,
		})

		local capabilities = require("cmp_nvim_lsp").default_capabilities()

		require("mason").setup()
		require("mason-lspconfig").setup({
			ensure_installed = {},
			handlers = {
				function(server_name)
					require("lspconfig")[server_name].setup({
						capabilities = capabilities,
					})
				end,
			},
		})
	end,
}



return
{
	"echasnovski/mini.nvim",
	config = function()
		require("mini.ai").setup({ n_lines = 500 })
		require("mini.surround").setup()
		local statusline = require("mini.statusline")
		statusline.setup({ use_icons = vim.g.have_nerd_font })
		statusline.section_location = function()
			return "%2l:%-2v"
		end
	end,
}



return
{
	"https://github.com/t-troebst/perfanno.nvim",
	config = function()
		require("perfanno").setup {
			-- List of highlights that will be used to highlight hot lines (or nil to disable).
			line_highlights = require("perfanno.util").make_bg_highlights(nil, "#FF0000", 10),
			-- Highlight used for virtual text annotations (or nil to disable virtual text).
			vt_highlight = require("perfanno.util").make_fg_highlight("#FF0000"),

			-- Annotation formats that can be cycled between via :PerfCycleFormat
			--   "percent" controls whether percentages or absolute counts should be displayed
			--   "format" is the format string that will be used to display counts / percentages
			--   "minimum" is the minimum value below which lines will not be annotated
			-- Note: this also controls what shows up in the telescope finders
			formats = {
				{ percent = true,  format = "%.2f%%", minimum = 0.5 },
				{ percent = false, format = "%d",     minimum = 1 }
			},

			-- Automatically annotate files after :PerfLoadFlat and :PerfLoadCallGraph
			annotate_after_load = true,
			-- Automatically annotate newly opened buffers if information is available
			annotate_on_open = true,

			-- Options for telescope-based hottest line finders
			telescope = {
				-- Enable if possible, otherwise the plugin will fall back to vim.ui.select
				enabled = pcall(require, "telescope"),
				-- Annotate inside of the preview window
				annotate = true,
			},

			-- Node type patterns used to find the function that surrounds the cursor
			ts_function_patterns = {
				-- These should work for most languages (at least those used with perf)
				default = {
					"function",
					"method",
				},
				-- Otherwise you can add patterns for specific languages like:
				-- weirdlang = {
				--     "weirdfunc",
				-- }
			}
		}

		local telescope = require("telescope")
		local actions = telescope.extensions.perfanno.actions
		telescope.setup {
			extensions = {
				perfanno = {
					-- Special mappings in the telescope finders
					mappings = {
						["i"] = {
							-- Find hottest callers of selected entry
							[""] = actions.hottest_callers,
							-- Find hottest callees of selected entry
							[""] = actions.hottest_callees,
						},

						["n"] = {
							["gu"] = actions.hottest_callers,
							["gd"] = actions.hottest_callees,
						}
					}
				}
			}
		}
	end
}



return
{
	"nvim-telescope/telescope.nvim",
	event = "VimEnter",
	branch = "0.1.x",
	dependencies = {
		"nvim-lua/plenary.nvim",
		{
			"nvim-telescope/telescope-fzf-native.nvim",
			build = "make",
			cond = function()
				return vim.fn.executable("make") == 1
			end,
		},
		{ "nvim-telescope/telescope-ui-select.nvim" },
		{ "nvim-tree/nvim-web-devicons",            enabled = vim.g.have_nerd_font },
	},
	config = function()
		require("telescope").setup({
			pickers = {
				colorscheme = {
					enable_preview = true,
				},
			},
			extensions = {
				["ui-select"] = {
					require("telescope.themes").get_dropdown(),
				},
			},
			defaults = {
				mappings = {
					-- i = {
					-- 	[""] = require('telescope.actions').close,
					-- },
					n = {
						[""] = require('telescope.actions').close,
					},
				},
			},
		})
		pcall(require("telescope").load_extension, "fzf")
		pcall(require("telescope").load_extension, "ui-select")
		local builtin = require("telescope.builtin")
		vim.keymap.set("n", "sh", builtin.help_tags, { desc = "[S]earch [H]elp" })
		vim.keymap.set("n", "sk", builtin.keymaps, { desc = "[S]earch [K]eymaps" })
		vim.keymap.set("n", "sf", builtin.find_files, { desc = "[S]earch [F]iles" })
		vim.keymap.set("n", "ss", builtin.builtin, { desc = "[S]earch [S]elect Telescope" })
		vim.keymap.set("n", "sw", builtin.grep_string, { desc = "[S]earch current [W]ord" })
		vim.keymap.set("n", "sg", builtin.live_grep, { desc = "[S]earch by [G]rep" })
		vim.keymap.set("n", "sd", builtin.diagnostics, { desc = "[S]earch [D]iagnostics" })
		vim.keymap.set("n", "sr", builtin.resume, { desc = "[S]earch [R]esume" })
		vim.keymap.set("n", "s.", builtin.oldfiles, { desc = '[S]earch Recent Files ("." for repeat)' })
		vim.keymap.set("n", "", builtin.buffers, { desc = "[ ] Find existing buffers" })
		vim.keymap.set("n", "/", function()
			builtin.current_buffer_fuzzy_find(require("telescope.themes").get_dropdown({
				winblend = 10,
				previewer = true,
			}))
		end, { desc = "[/] Fuzzily search in current buffer" })
		vim.keymap.set("n", "s/", function()
			builtin.live_grep({
				grep_open_files = true,
				prompt_title = "Live Grep in Open Files",
			})
		end, { desc = "[S]earch [/] in Open Files" })
		vim.keymap.set("n", "sn", function()
			builtin.find_files({ cwd = vim.fn.stdpath("config") })
		end, { desc = "[S]earch [N]eovim files" })
	end,
}



